insert into coctail values (2, 'Алкогольный коктейль кубинского происхождения, основными компонентами которого являются светлый ром, сок лайма и сахар. Классифицируется как аперитив. Входит в число официальных коктейлей Международной ассоциации барменов (IBA), категория «Незабываемые». Сходный по составу бразильский коктейль называется кайпиринья. Один из самых популярных в мире коктейлей.', 'initial_data/daikiri.jpg', 'Дайкири', 1);
