insert into ingredient values (220, 'Холодный персиковый чай', 'NON_ALCH_DRINK', 'initial_data/sprite.jpg', 'Фруктовый холодный чай производят на основе экстракта чайного листа с добавлением персикового сиропа без искусственных ароматизаторов и консервантов.', 1);
insert into ingredient values (221, 'Апельсин', 'FRUIT', 'initial_data/orange.jpg', 'Оранжевый цитрусовый фрукт, растущий на высоких деревьях преимущественно в Бразилии, США и Китае. Содержит витамин С, калий и кальций, оказывающие положительное влияние на работу сердца, сосудов и организма в целом. Один плод весит в среднем 300 г и дает 80 мл сока, разрезается на 10 кружков или 8 долек.', 1);
insert into ingredient values (222, 'Лимон', 'FRUIT', 'initial_data/lemon.jpg', 'Тропический цитрусовый фрукт, растущий на небольших вечнозеленых деревьях преимущественно в Китае, Индии и Мексике. Содержит витамин С и органические кислоты, незаменим при авитаминозах и простуде. Один плод весит 160 г и дает 60 мл сока, разрезается на 8 кружков или 4 крупные дольки по 15 мл каждая.', 1);
