insert into ingredient values (225, 'Грейпфрут', 'FRUIT', 'initial_data/lime.jpg', 'Популярный цитрусовый фрукт, растущий кластерами на деревьях преимущественно в Китае, США и Мексике. Содержит витамин С, пектин и ликопен, снижающий уровень холестерина. Один плод весит в среднем 600 г, разрезается на 10 кружков или долек и дает 200 мл сока.',  1);
insert into ingredient values (226, 'Ананас', 'FRUIT', 'initial_data/lime.jpg', 'Большой тропический фрукт, растущий на наземных колючих растениях преимущественно в Бразилии, Таиланде и на Филиппинах. Содержит марганец и витамин С, стимулирующие пищеварение и снижающие вязкость крови. Один плод весит в среднем 1200 г и разрезается примерно на 40 кружков.',  1);
insert into ingredient values (227, 'Маракуйя', 'FRUIT', 'initial_data/lime.jpg', 'Субтропический фрукт страсти, растущий на вечнозеленых лианах преимущественно в Новой Зеландии, Израиле и на Шри-Ланке. Содержит витамин А, бета-каротин и калий, обладает тонизирующим эффектом. Один плод весит в среднем 90 г.',  1);
insert into ingredient values (228, 'Мандарин', 'FRUIT', 'initial_data/lime.jpg', 'Тропический цитрусовый фрукт, растущий на вечнозеленых кустарниках преимущественно в Китае, Испании и Бразилии. Содержит витамин С и калий, способствующие укреплению иммунитета. Один плод весит в среднем 100 г и дает 40 мл сока.',  1);
insert into ingredient values (229, 'Гранат', 'FRUIT', 'initial_data/lime.jpg', 'Средиземноморский фрукт, растущий на небольших лиственных кустарниках преимущественно в Израиле и Индии. Содержит калий и фосфор, положительно влияющие на сердечно-сосудистую систему. Один плод весит в среднем 300 г и разрезается на 10 долек.',  1);
insert into ingredient values (230, 'Манго', 'FRUIT', 'initial_data/lime.jpg', 'Дикий тропический фрукт, растущий на деревьях преимущественно в Индии, Пакистане и на Филиппинах. Содержит витамин А, калий и фолиевую кислоту, улучшающие иммунитет и зрение. Один плод весит в среднем 300 г и разрезается на 10 долек.',  1);
insert into ingredient values (231, 'Киви', 'FRUIT', 'initial_data/lime.jpg', 'Китайский крыжовник растет на крупных древовидных лианах преимущественно в Италии, Новой Зеландии и Чили. Содержит витамины С, клетчатку, фолиевую кислоту, кальций и фосфор, усиливающие иммунную систему человека. Одна ягода весит в среднем 120 г.',  1);
insert into ingredient values (232, 'Банан', 'FRUIT', 'initial_data/lime.jpg', 'Древнейшее тропическое травянистое растение, произрастающее преимущественно в Индии, Китае и Уганде. Фрукт содержит витамин B6, марганец и пищевые волокна. Один плод весит в среднем 200 г, из которых 80 г – кожура.',  1);
insert into ingredient values (233, 'Персик', 'FRUIT', 'initial_data/lime.jpg', 'Солнечный фрукт, растущий на лиственных деревьях преимущественно в Китае, Италии и Испании. Содержит органические кислоты, минеральные соли и пектины. Один плод весит в среднем 120 г, из которых можно получить 80 г пюре.',  1);
insert into ingredient values (234, 'Груша', 'FRUIT', 'initial_data/lime.jpg', 'Фрукт умеренного климата, растущий на деревьях пирамидальной формы преимущественно в Италии, Аргентине и Турции. Содержит холин, калий, витамины С и К. Один плод весит в среднем 150 г.',  1);
insert into ingredient values (235, 'Абрикос', 'FRUIT', 'initial_data/lime.jpg', 'Солнечный фрукт, растущий на деревьях преимущественно в Турции, Иране и Узбекистане. Содержит большое количество каротина и солей калия, полезных для почек и сердечно-сосудистой системы. Один плод весит около 60 г, из которых можно получить 40 г пюре.',  1);
insert into ingredient values (236, 'Мята', 'HERBS', 'initial_data/lime.jpg', 'Самая разрекламированная трава в коктейльном мире насчитывает более 30 видов. В миксологии используют преимущественно перечную и сладкую мяту. Хранится в холодильнике в бумажном полотенце не более двух суток.',  1);
insert into ingredient values (237, 'Розмарин', 'HERBS', 'initial_data/lime.jpg', 'Средиземноморская трава с пряным ароматом сосны.',  1);
insert into ingredient values (238, 'Зеленый базилик', 'HERBS', 'initial_data/lime.jpg', 'Пряно-ароматная трава, растущая преимущественно в теплых областях Старого Света. Зеленые листья завоевали особую популярность благодаря ставшему уже классическим коктейлю «Базиликовый удар».',  1);
insert into ingredient values (239, 'Ананасовые листья', 'HERBS', 'initial_data/lime.jpg', 'Листья тропического фрукта используют как украшение для экзотических тики-коктейлей.',  1);
insert into ingredient values (240, 'Лимонная трава', 'HERBS', 'initial_data/lime.jpg', 'Трава с цитрусовым ароматом из карибской и азиатской кухни, растет преимущественно на Британских островах, Филиппинах и в Индонезии.',  1);
insert into ingredient values (241, 'Лепестки роз', 'HERBS', 'initial_data/lime.jpg', 'Самый продаваемый цветок в мире. В одном бутоне – 25 лепестков.',  1);
insert into ingredient values (242, 'Красный базилик', 'HERBS', 'initial_data/lime.jpg', 'Пряно-ароматная трава, растущая преимущественно в теплых областях Старого Света.',  1);
insert into ingredient values (243, 'Цветы фиалки', 'HERBS', 'initial_data/lime.jpg', 'Яркий съедобный цветок – символ французского города Тулузы. Растет преимущественно в северном полушарии.',  1);
insert into ingredient values (244, 'Лаймовые листья', 'HERBS', 'initial_data/lime.jpg', 'Листья тропического цитрусового в Азии используют как приправу, а также добавляют в качестве ароматизатора в коктейли.',  1);
insert into ingredient values (245, 'Тимьян', 'HERBS', 'initial_data/lime.jpg', 'Ароматическая пряность из веточек низкорослых кустарников, растущих преимущественно в Евразии, Северной Африке и Гренландии. Входит в состав прованской смеси, идеально подходит для ароматизации крепких спиртов.',  1);
insert into ingredient values (246, 'Орхидея', 'HERBS', 'initial_data/lime.jpg', 'Красивый цветок, насчитывающий более 25 000 видов, растет на всех континентах земного шара. Цветки используют как украшение, а на стручках из рода ванили настаивают крепкие спирты и сиропы.',  1);
insert into ingredient values (247, 'Кориандр', 'HERBS', 'initial_data/lime.jpg', 'Пряная ароматная трава играла большую роль в ритуальных жертвоприношениях в Древнем Египте. Придает особый свежий и острый аромат настоянным на ней крепким спиртам.',  1);
insert into ingredient values (248, 'Щавель', 'HERBS', 'initial_data/lime.jpg', 'Многолетние травы и полукустарники с продолговатыми листьями, растущие на территории Евразии, Африки, Северной и Южной Америки. Придают напитку кислинку.',  1);
insert into ingredient values (249, 'Шалфей', 'HERBS', 'initial_data/lime.jpg', 'Колокольчатый кустарник, растущий во всех частях Старого и Нового Света, служит ароматической добавкой, придающей напитку горько-пряные ноты.',  1);
insert into ingredient values (250, 'Эстрагон', 'HERBS', 'initial_data/lime.jpg', 'Многолетнее травянистое растение, растущее в Восточной Европе, Средней Азии и Северной Америке. Широко используется в лимонадах.',  1);
insert into ingredient values (251, 'Руккола', 'HERBS', 'initial_data/lime.jpg', 'Яркая трава с выраженным пряно-горчичным вкусом произрастает преимущественно Индии, Африке и Южной Европе. Широко распространена в кухне Средиземноморья.',  1);
insert into ingredient values (252, 'Орегано', 'HERBS', 'initial_data/lime.jpg', 'Пряная трава, произрастающая преимущественно на территории Европы. Применяется в качестве приправы и украшения.',  1);
insert into ingredient values (253, 'Табаско соус красный', 'SAUCE_OIL', 'initial_data/lime.jpg', 'Красный перечный соус производят из отборных спелых перцев чили, выдержанных в течение трех лет в бочках из лимузенского дуба в соли с Эйвери Айленд. Полученную основу сдабривают уксусом и оставляют еще на четыре недели. На вкус более острый, чем его зеленая версия.',  1);
insert into ingredient values (254, 'Ворчестер соус', 'SAUCE_OIL', 'initial_data/lime.jpg', 'Знаменитый вустерширский соус производят по секретной рецептуре начала XIX века. В его состав входит более десятка ингредиентов, выдержанных в бочках 18 месяцев: томатная паста, уксус, сахар, рыба, черный перец и чили, имбирь и мускат для придания пикантности.',  1);
insert into ingredient values (255, 'Красный винный уксус', 'SAUCE_OIL', 'initial_data/lime.jpg', 'Ягодный уксус, получаемый методом сбраживания красного винограда или виноградного сока в специальных циркуляционных аппаратах.',  1);
insert into ingredient values (256, 'Устричный соус', 'SAUCE_OIL', 'initial_data/lime.jpg', 'Густой коричневый соус из протертого устричного мяса, соли и соевого соуса. Служит пикантной приправой.',  1);
insert into ingredient values (257, 'Соевый соус', 'SAUCE_OIL', 'initial_data/lime.jpg', 'Соленый азиатский соус готовят методом ферментации соевых бобов под воздействием грибов из рода аспергилл.',  1);
insert into ingredient values (258, 'Шоколадный соус', 'SAUCE_OIL', 'initial_data/lime.jpg', 'Сладкий шоколадный соус производят из отборных какао-зерен с добавлением сахарного сиропа.',  1);
insert into ingredient values (259, 'Карамельный соус', 'SAUCE_OIL', 'initial_data/lime.jpg', 'Сладкий соус производят из обезжиренного сгущенного молока с добавлением сахарного сиропа и карамельного ароматизатора.',  1);
insert into ingredient values (260, 'Лед в кубиках', 'ICE', 'initial_data/ice-cubes.jpg', 'При производстве этого незаменимого ингредиента основное внимание уделяют чистоте воды и скорости заморозки. Чем медленнее замораживается вода, тем прозрачнее получается продукт. Идеальные образцы не имеют полостей и медленнее тают в бокале.', 1);
insert into ingredient values (261, 'Дробленый лед', 'ICE', 'initial_data/ice-cubes.jpg', 'Первый «бритый» лед бармены срезали специальными ножами с больших ледяных плит. Позже появились дробильные машинки и ледогенераторы.', 1);
insert into ingredient values (262, 'Ледяной куб', 'ICE', 'initial_data/ice-cubes.jpg', 'Большие кубы вернулись в бар с возвращением моды на классические напитки. При производстве сначала замораживают большие ледяные блоки, которые впоследствии разрезают пилой на ровные кубы. Лёд такой формы медленнее тает в бокале и не разбавляет напиток.', 1);
insert into ingredient values (263, 'Ледяная сфера', 'ICE', 'initial_data/ice-cubes.jpg', 'Гордость японских барменов – лед, возведенный в ранг предмета искусства. Сегодня все чаще появляется в барах благодаря специальным машинкам. В легко нагреваемые тиски помещают заготовленный ледяной куб, металл разогревают водой и обрезают лед по заранее заготовленному шарообразному контуру.', 1);
insert into ingredient values (264, 'Сухой лед', 'ICE', 'initial_data/ice-cubes.jpg', 'Молекулярный ингредиент, в твердом состоянии состоит из двуокиси углерода. При разморозке и добавлении воды превращается в пар, минуя жидкую фазу.', 1);
insert into ingredient values (265, 'Бренди', 'STRONG_ALCOHOL', '', 'Бренди', 1);
insert into ingredient values (266, 'Сливки', 'OTHER', '', 'Производят преимущественно из коровьего молока методом частичной сепарации – отделения сливок от молока. Хранится в холодильнике не более 20 дней.', 1);
insert into ingredient values (267, 'Куантро', 'LIQUEUR', '', 'Куантро', 1);
insert into ingredient values (268, 'Яичный белок', 'OTHER', '', 'Чтобы придать коктейлю с этим ингредиентом нежную и пенистую текстуру рекомендуется использовать метод приготовления «драй шейк».', 1);
